package namit.retail_app.address.presentation.adapterimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport namit.retail_app.address.Rimport namit.retail_app.core.data.entity.AddressSearchModelimport kotlinx.android.synthetic.main.item_address_search.view.*import kotlin.properties.Delegatesclass SearchAddressAdapter : RecyclerView.Adapter<SearchAddressAdapter.AddressSearchHolder>() {    var items by Delegates.observable(listOf<AddressSearchModel>()) { _, _, _ ->        notifyDataSetChanged()    }    var onItemSelected: (address: AddressSearchModel) -> Unit = {}    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): AddressSearchHolder {        val view = LayoutInflater.from(parent.context).inflate(R.layout.item_address_search, parent, false)        return AddressSearchHolder(view)    }    override fun getItemCount(): Int {        return items.size    }    override fun onBindViewHolder(holder: AddressSearchHolder, position: Int) {        holder.bind(items[position])    }    inner class AddressSearchHolder(view: View) : RecyclerView.ViewHolder(view) {        init {            itemView.setOnClickListener {                if (adapterPosition != RecyclerView.NO_POSITION) {                    onItemSelected(items[adapterPosition])                }            }        }        fun bind(address: AddressSearchModel) {            itemView.addressTitleTextView.text = address.addressName            itemView.fullAddressTextView.text = address.addressDetail        }    }}