package namit.retail_app.address.presentation.searchimport androidx.lifecycle.MutableLiveDataimport namit.retail_app.core.data.entity.AddressSearchModelimport namit.retail_app.core.presentation.base.BaseViewModelimport namit.retail_app.core.utils.UseCaseResultimport namit.retail_app.maps.domain.SearchAddressUseCaseimport kotlinx.coroutines.launchclass SearchLocationViewModel(    private val searchAddressUseCase: SearchAddressUseCase) : BaseViewModel() {    val addressSearchLiveData = MutableLiveData<List<AddressSearchModel>>()    fun searchAddress(query: String) {        launch {            val response = searchAddressUseCase.execute(query)            if (response is UseCaseResult.Success) {                addressSearchLiveData.value = response.data            }        }    }}