package namit.retail_app.maps.data.repositoryimport namit.retail_app.maps.BuildConfigimport namit.retail_app.maps.data.api.MapsApiimport namit.retail_app.maps.data.response.SearchLocationResponseinterface SearchAddressRepository {    suspend fun search(query: String): SearchLocationResponse}class SearchAddressRepositoryImpl(private val api: MapsApi) :    SearchAddressRepository {    override suspend fun search(query: String): SearchLocationResponse {        return api.searchLocation(            query = query,            key = BuildConfig.GOOGLE_CLOUD_SEARCH_PLACE_API_KEY        )    }}